mapper.update:
UPDATE test
SET test_title = #{testTitle}
WHERE test_no = #{testNo}

mapper.insert:
INSERT INTO test(
	test_title 
	, test_date
) VALUES(
	#{testTitle}
	, #{testDate}
);

mapper.select:
SELECT COUNT(*) FROM test
test_title LIKE CONCAT('%',#{searchWord},'%')
AND test_date <= #{todayDate}

SELECT test_no testNo , test_title testTitle , test_date testDate FROM test
AND test_title LIKE CONCAT('%',#{searchWord},'%')
AND test_date <= #{todayDate}
ORDER BY test_date DESC LIMIT #{beginRow}, #{rowPerPage};
